
public class test2 {
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 50;
		int b = 10 ;
		
		
		int c	= a + b ;
		
		
		int d	= a - b ;
		
		
		int e	= a * b ;
		
		
		int f	= a / b ;
		
		
		System.out.println( a + "+" + b +" = " + c );
		System.out.println( a + "-" + b +" = " + d );
		System.out.println( a + "*" + b +" = " + e );
		System.out.println( a + "/" + b +" = " + f );

		
		
		
		
		
		
		
		

	}

}
